luaunit = require('luaunit')function verificaBarcos (tabuleiro)	local contador = 0	for linha=1,10 do		for coluna=1,10 do			if(tabuleiro[linha][coluna] == 1) then				contador = contador + 1			end		end		return contador	endendTestesTabuleiro{} --classe de testes	function TestesTabuleiro:verificaSeTabuleiroTemNavios()		luaunit.assertEquals(verificaBarcos(t),3)os.exit( luaunit.LuaUnit.run())